var showLeftPush = $('#showLeftPush'),
    menuLeft = $('#bootshape-menu-s1'),
    body = $(document.body);

showLeftPush.on('click', function() {
    $(this).toggleClass('active');
    body.toggleClass('bootshape-menu-push-toright');
    menuLeft.toggleClass('bootshape-menu-open');
});

snippets.css:
html,
body {
  min-width: 100%;
  min-height: 100%;
  background: url('img/fabric_of_squares_gray_@2X.png');
  background-repeat: repeat;
  font-family: "Carrois Gothic";
}
.inline-block {
  display: inline-block;
}
.relative {
  position: relative;
}
.footer {
  padding: 10px;
}
.footer a {
  color: #d74900;
}
.bootshape {
  padding-top: 40px;
}
.bootshape .main {
  background: #fff;
}
.bootshape .main .head {
  padding: 15px 30px;
  background: url('img/bg.jpg');
  background-size: cover;
}
.bootshape .main .head .person {
  font-size: 22px;
  color: #fff;
}
.bootshape .main .head .person img {
  height: 60px;
  border-radius: 55em;
}
.bootshape .main .head .icons {
  font-size: 20px;
  line-height: 60px;
  color: #9e9489;
  font-family: 'Alegreya Sans';
}
.bootshape .main .head .icons span {
  cursor: pointer;
}
.bootshape .main .head .icons span:hover {
  color: #e17239;
}
.bootshape .main .navigation {
  background: #d74900;
  overflow-x: scroll;
  overflow-y: hidden;
}
.bootshape .main .navigation table {
  margin: 0;
}
.bootshape .main .navigation td {
  border-top: none !important;
  white-space: nowrap;
  padding: 0;
}
.bootshape .main .navigation td a {
  position: relative;
  display: block;
  width: 100%;
  padding: 8px;
  color: #fff;
  text-decoration: none;
  font-size: 16px;
}
.bootshape .main .navigation td a:hover {
  background: #e1753e;
}
.bootshape .main .navigation td a.current:hover:after {
  border-top-color: #e17239;
}
.bootshape .main .navigation td a.current:after {
  content: "";
  position: absolute;
  top: 100%;
  width: 4px;
  border: 4px solid transparent;
  border-top: 4px solid #d74900;
  left: 50%;
  margin-left: -2px;
}
.bootshape .main .navigation td + td {
  border-left: 1px solid #e17239;
}
.bootshape .main .search-form {
  margin: 15px 30px;
  font-family: 'Alegreya Sans';
}
.bootshape .main .search-form input {
  padding-left: 35px;
  border-radius: 20px;
}
.bootshape .main .search-form span {
  left: 0;
  top: 0;
}
.bootshape .main .pagination {
  font-family: 'Alegreya Sans';
  margin: 0 0 5px 0;
}
.bootshape .main .pagination li {
  font-size: 16px;
}
.bootshape .main .pagination li.active a {
  border: 1px solid #d74900;
  background: none;
  color: #d74900;
}
.bootshape .main .pagination li:hover a {
  border: 1px solid #d74900;
  background: none;
  color: #d74900;
}
.bootshape .main .pagination li a {
  padding: 4px 11px;
  margin: 0 2px;
  border-radius: 55em;
  border: none;
  color: #8a8a8a;
  border: 1px solid transparent;
}
.bootshape .main .list img {
  height: 60px;
  border-radius: 55em;
  position: relative;
  top: -20px;
}
.bootshape .main .list td {
  padding: 20px 30px;
  border-top: none !important;
  vertical-align: middle;
  position: relative;
  cursor: pointer;
}
.bootshape .main .list td .user-info {
  padding-left: 15px;
  display: inline-block;
}
.bootshape .main .list td .user-info h2 {
  margin: 0 0 5px 0;
}
.bootshape .main .list td .user-info .icons {
  color: #8a8a8a;
}
.bootshape .main .list td .user-info .icons span {
  margin-right: 5px;
}
.bootshape .main .list td .triangle {
  background-image: url('img/triangle.png');
  background-size: cover;
  width: 20px;
  height: 33px;
  position: absolute;
  right: 0px;
  top: 23px;
}
@media (min-width: 768px) {
  .bootshape .main {
    width: 80%;
  }
}
@media (min-width: 992px) {
  .bootshape .main {
    width: 50%;
  }
}
@media (min-width: 1200px) {
  .bootshape .main {
    width: 40%;
  }
}
@media (max-width: 768px) {
  .bootshape {
    padding: 0;
  }
  .bootshape .main {
    width: 100%;
  }
  .bootshape .main .navigation {
    font-size: 12px;
  }
}

.bootshape {
  font-family: 'Alef', sans-serif;
  background-color: #FFF;
  text-align: center;
  color: #333;
}
.bootshape a,
.bootshape a:hover,
.bootshape a:focus {
  color: #dd745c;
}
.bootshape h1 {
  color: #dd745c;
  font-family: 'Alef', sans-serif;
  padding-bottom: 20px;
  font-weight: bold;
}
.bootshape .form-control {
  background-color: #f1f0f0;
  border-radius: 0;
  border: none;
  margin: 0 0 10px 0;
  color: #333;
  box-shadow: none;
  height: 62px;
  font-family: 'Alef', sans-serif;
  text-align: center;
  font-size: 22px;
}
.bootshape .form-control:focus {
  border-color: #e4e0e0;
  outline: 0;
  -webkit-box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0.015), 0 0 8px rgba(228, 224, 224, 0.6);
  -moz-box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0.015), 0 0 8px rgba(228, 224, 224, 0.6);
  box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0.015), 0 0 8px rgba(228, 224, 224, 0.6);
}
.bootshape .btn-lg {
  font-size: 22px;
  height: 62px;
  border-radius: 0;
  background-color: #dd745c;
  border: 0;
  text-transform: uppercase;
}
.bootshape .btn-primary:hover,
.bootshape .btn-primary:focus {
  background-color: #ba523a;
}
.bootshape .forgot-password {
  padding-top: 20px;
  text-decoration: underline;
  color: #333;
  font-size: 18px;
}
.bootshape .forgot-password a {
  color: #333;
}
.bootshape .footer {
  padding: 20px 0;
}
.bootshape .simple-login {
  margin: 0 auto;
  max-width: 500px;
  padding: 100px 15px 15px 15px;
}

body {
  background: #f1f1f1;
  padding: 10px;
}
.thumbnails {
  font-family: 'Fauna One', serif;
}
.thumbnails h1 {
  font-family: 'Stint Ultra Expanded', cursive;
}
.thumbnails .thumbnail {
  padding: 0px;
  border-radius: 0;
  border: none;
  -webkit-box-shadow: 0 1px 2px rgba(0, 0, 0, 0.1);
  -moz-box-shadow: 0 1px 2px rgba(0, 0, 0, 0.1);
  box-shadow: 0 1px 2px rgba(0, 0, 0, 0.1);
}
.thumbnails .thumbnail .image-container {
  overflow: hidden;
  width: 100%;
  height: 100%;
}
.thumbnails .thumbnail .image-container img {
  -webkit-transition: all 0.4s;
  -moz-transition: all 0.4s;
  -o-transition: all 0.4s;
  transition: all 0.4s;
}
.thumbnails .thumbnail .image-container img:hover {
  -webkit-transform: scale(1.2, 1.2);
  -moz-transform: scale(1.2, 1.2);
  -o-transform: scale(1.2, 1.2);
  -ms-transform: scale(1.2, 1.2);
  transform: scale(1.2, 1.2);
}
.thumbnails .date {
  padding: 5px 10px;
  color: #ff7e00;
}
.thumbnails a {
  color: #ff7e00;
}

html,
body {
  height: 100%;
}
.bootshape.login-form {
  height: 100%;
  background: url('./img/background.jpg');
  background-size: cover;
  font-family: 'Balthazar', serif;
}
.bootshape.login-form h1 {
  font-family: 'Stalemate', cursive;
}
.bootshape.login-form a {
  color: #fec000;
}
.bootshape.login-form .login-popup-wrap {
  position: relative;
  top: 80px;
  left: 60px;
  color: #aeaeae;
  min-width: 300px;
}
.bootshape.login-form .login-popup-wrap > :first-child {
  margin-bottom: 6px;
}
.bootshape.login-form .login-popup {
  -webkit-box-shadow: 0px 0px 6px rgba(0, 0, 0, 0.2);
  -moz-box-shadow: 0px 0px 6px rgba(0, 0, 0, 0.2);
  box-shadow: 0px 0px 6px rgba(0, 0, 0, 0.2);
  background: #fff;
  border-radius: 3px;
  padding: 20px 40px;
}
.bootshape.login-form .title {
  margin-bottom: 40px;
  font-size: 48px;
  color: #333333;
}
.bootshape.login-form .text {
  background: #f8f8f8;
  border: none;
  font-size: 16px;
}
.bootshape.login-form .submit {
  background: #fec000;
  color: #fff;
  font-size: 24px;
  width: 100%;
  padding: 0;
}
.bootshape.login-form .divider {
  height: 1px;
  margin-top: 30px;
  border-bottom: 1px solid #e2e2e2;
}
.bootshape.login-form .divider-label {
  display: inline-block;
  padding: 6px;
  background: #fff;
  position: relative;
  top: -16px;
}
.bootshape.login-form .footer-icons {
  margin: 15px 0;
}
.bootshape.login-form .footer-icons li > a {
  display: block;
  border-radius: 55em;
  background: #aeaeae;
  width: 32px;
  height: 32px;
  padding: 4px 0;
  font-size: 20px;
  color: #fff;
}
@media (max-width: 767px) {
  .bootshape.login-form .login-popup-wrap {
    position: relative;
    top: 0px;
    left: 0px;
    padding: 50px 50px;
  }
  .login-popup {
    padding: 20px 5px;
  }
}
@media (max-width: 460px) {
  .bootshape.login-form .login-popup-wrap {
    padding: 50px 5px;
  }
}
